targets.expr:
sum by (alert_group)(
  count_over_time(
    alert_summary{
      alert_severity!="Info",
      device_id=~"$device_id",
      location_name=~"$location"
    }[$__range]
  )
)

sum by (alert_severity)(
  count_over_time(
    alert_summary{
      alert_severity!="Info",
      device_id=~"$device_id",
      alert_group="Security",
      alert_category=~"error|warning",
      location_name=~"$location"
    }[$__range]
  )
)

